import React,{Component} from 'react';
import styled from 'styled-components';

export default class Portfolio extends Component {
  state= {
    portfolio : []
  }
  
  componentDidMount(){
    
  }
  render(){

    return(
      <section className=''>
        <Header> My Portfolio</Header>
        <Container>
          <Box>
            <img src='/images/portfolio-img1.jpg' alt='' />
            <div className='layer flex-full'>
              <p> Show Image</p>
            </div>
          </Box>
          <Box>
            <img src='/images/portfolio-img2.jpg' alt='' />
            <div className='layer flex-full'>
              <p> Show Image</p>
            </div>
          </Box>
          <Box>
            <img src='/images/portfolio-img3.jpg' alt='' />
            <div className='layer flex-full'>
              <p> Show Image</p>
            </div>
          </Box>
          <Box>
            <img src='/images/portfolio-img4.jpg' alt='' />
            <div className='layer flex-full'>
              <p> Show Image</p>
            </div>
          </Box>
          <Box>
            <img src='/images/portfolio-img5.jpg' alt='' />
            <div className='layer flex-full'>
              <p> Show Image</p>
            </div>
          </Box>
          <Box>
            <img src='/images/portfolio-img6.jpg' alt='' />
            <div className='layer flex-full'>
              <p> Show Image</p>
            </div>
          </Box>
          <Box>
            <img src='/images/portfolio-img7.jpg' alt='' />
            <div className='layer flex-full'>
              <p> Show Image</p>
            </div>
          </Box>
          <Box>
            <img src='/images/portfolio-img8.jpg' alt='' />
            <div className='layer flex-full'>
              <p> Show Image</p>
            </div>
          </Box>
        </Container>
      </section>

    )
  }
}

const Header = styled.h2`
  text-align: center;
`
const Container = styled.div`
  display: grid;
  grid-template-columns: repeat( 4 , 1fr);
  gap:0px;
  margin-top: 30px;
  
`
const Box = styled.div`
  {
    position: relative;
  }
  img{
    height: 300px;
    max-width: 100%;
    position: relative;
    transition: 0.3s;
    cursor:pointer;
    display: block;
    
  }
  .layer{
    position: absolute;
    top:0;
    left:0;
    height: 100%;
    width: 100%;
    z-index: 2;
    background-color: rgba(255, 166, 0, 0.411);
    justify-content: center;
    display: none;
    p{
      padding: 5px;
      background-color: white;
      cursor: pointer;
    }

    
  }

  &:hover .layer{
    display: flex;
  }
  
`
