import React, { Component } from 'react'
import './addItem.css';

export default class AddItem extends Component {
   state={
    subject : '',
    lesson : '',
   } 
   handleChange = (e)=>{
    this.setState({
      [e.target.id] : e.target.value
    })
   }
   
   addItem = (e)=>{
    console.log(this.State)
   }

  render() {
    return (
      <>
        <tr>
           <td>
             <input placeholder='subject name' type='text' id='subject' onChange={this.handleChange} />
           </td>
         
           <td>
             <input placeholder='lesson name' type='text' id='lesson' onChange={this.handleChange} />
            </td> 
         
           <td> <span onClick={this.addItem}> Add Item </span></td> 
        </tr>
        
       
      </>
    )
  }
}
