import React from 'react';
import './todoitem.css';

function TodoItem(props) {
    const {items , deleteItem} = props;
    let length = items.length;
  return (
    <div>
        
            <table>
                <tr>
                    <th> Subject </th>
                    <th> Lesson </th>
                    <th> Remove </th>
                </tr>
                { length ? 
                    items.map((item)=>(    
                        <tr key={item.id} >
                            <td>{item.subject}</td>
                            <td>{item.lesson}</td>
                            <td onClick={ ()=> deleteItem(item.id)} className='clear'> Clear </td>
                        </tr>
                        ))

                    : <tr style> NO ITEMS</tr>    
                }
            </table>
        
    </div>
  )
}

export default TodoItem
